function init(){}function anchor(){var n=[...document.querySelectorAll(".anchor-pin")],o=[...document.querySelectorAll(".anchor_txt")],t=window.scrollY+60,c=n.map(n=>n.getBoundingClientRect().top+window.scrollY);let e=0;for(let n=0;n<c.length;n++)t>=c[n]&&(e=n);o.forEach((n,o)=>n.classList.toggle("active",o===e))}window.onload=function(){init(),anchor()},window.addEventListener("scroll",()=>{anchor()});